import React from 'react';

const Movieinfo = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div style={style.overlay}>
      <div style={style.modal}>
        <button style={style.closeButton} onClick={onClose}>Ã—</button>
        <div style={styles.container}>
      <div style={styles.row}>
        <div style={styles.column}>
          <p style={styles.text}>Row 1, Column 1</p>
        </div>
        <div style={styles.column}>
          <p style={styles.text}>Row 1, Column 2</p>
        </div>
      </div>
      <div style={styles.row}>
        <div style={styles.column}>
          <p style={styles.text}>Row 2, Column 1</p>
        </div>
        <div style={styles.column}>
          <p style={styles.text}>Row 2, Column 2</p>
        </div>
      </div>
      <div style={styles.row}>
        <div style={styles.column}>
          <p style={styles.text}>Row 3, Column 1</p>
        </div>
        <div style={styles.column}>
          <p style={styles.text}>Row 3, Column 2</p>
        </div>
      </div>
      <div style={styles.largeTextContainer}>
        <p style={styles.largeText}>This is the larger text space below the rows.</p>
      </div>
    </div>
      </div>
    </div>
  );
};

const style = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.7)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modal: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '5px',
    minWidth: '300px',
    minHeight: '200px',
    display: 'flex',
    flexDirection: 'column',
  },
  closeButton: {
    alignSelf: 'flex-end',
    background: 'none',
    border: 'none',
    fontSize: '20px',
    cursor: 'pointer',
  },
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    maxWidth: '600px',
    margin: 'auto',
  },
  row: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '10px',
  },
  column: {
    flex: '1',
    marginRight: '10px',
    // Adjust last column's margin in each row
    ':last-child': {
      marginRight: '0',
    },
  },
  text: {
    margin: '0',
  },
  largeTextContainer: {
    marginTop: '20px',
  },
  largeText: {
    fontSize: '18px',
    margin: '0',
  },
};

export default Movieinfo;
