import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { useState } from 'react'
import Navbar from "./Navbar"

export default function Summary() {
    const location = useLocation();
    const movie = location.state?.movie;
    const selectedShowtime = location.state?.selectedShowtime;
    const navigate = useNavigate();
    const { selectedSeats } = location.state || {};

    // Aggregate ticket types and their counts
    const ticketSummary = selectedSeats.reduce((acc, seat) => {
        if (acc[seat.type]) {
            acc[seat.type].count += 1;
            acc[seat.type].totalPrice += seat.price;
        } else {
            acc[seat.type] = { count: 1, totalPrice: seat.price };
        }
        return acc;
    }, {});

    // Calculate total price
    const totalPrice = Object.values(ticketSummary).reduce((acc, { totalPrice }) => acc + totalPrice, 0);

    return (
        <>
            <Navbar />
            <div className="flex items-center justify-center min-h-200 mt-6">
                <div className="w-full max-w-4xl p-8 space-y-8 bg-white shadow-lg rounded-xl">
                    <div className="flex min-h-full flex-1 flex-col justify-center">
                        <h2 className="text-center text-3xl font-bold leading-9 tracking-tight text-gray-900">
                            Order Summary
                        </h2>
                        <h2 className="text-center text-lg font-semibold leading-9 tracking-tight text-gray-900 mt-4">
                            {selectedSeats.length} x Tickets to {movie.name}
                        </h2>
                        <p className="text-center text-m text-gray-700 mt-2">
                            {selectedShowtime.name}
                        </p>
                        <ul className="divide-y divide-gray-100 flex justify-center flex-col items-center mt-10">
                            {Object.entries(ticketSummary).map(([type, { count, totalPrice }]) => (
                                <li 
                                    className="flex w-7/12 gap-x-6 py-8"
                                    key={type}
                                >
                                    <span className="text-m font-semibold leading-6 text-gray-900">
                                        {type}: {count} x ${totalPrice.toFixed(2)}
                                    </span>
                                </li>
                            ))}
                            <li>Total: ${totalPrice.toFixed(2)}</li>
                        </ul>
                    </div>
                </div>
            </div>
        </>
    )
}
