import React, { useState, useEffect } from 'react';

const Checkout = ({ movie, selectedShowtime, ticketCounts, ticketPrices }) => {
  const [promoCode, setPromoCode] = useState('');
  const [discount, setDiscount] = useState(0);
  const salesTaxRate = 0.06; // 6% sales tax
  const location = useLocation();

  // Calculate subtotal based on ticketCounts and ticketPrices
  const subtotal = Object.entries(ticketCounts).reduce((total, [type, count]) => {
    return total + (ticketPrices[type] * count);
  }, 0);

  // Apply discount based on promoCode
  useEffect(() => {
    if (promoCode === 'SAVE10') {
      setDiscount(subtotal * 0.1); // Example: 10% discount
    } else {
      setDiscount(0);
    }
  }, [promoCode, subtotal]);

  const totalBeforeTax = subtotal - discount;
  const salesTax = totalBeforeTax * salesTaxRate;
  const finalTotal = totalBeforeTax + salesTax;

  return (
    <div>
      <h2>Checkout for {movie.name}</h2>
      <p>Showtime: {selectedShowtime.name}</p>
      <p>Subtotal: ${subtotal.toFixed(2)}</p>
      <input
        type="text"
        value={promoCode}
        onChange={(e) => setPromoCode(e.target.value)}
        placeholder="Promotion Code"
      />
      <p>Discount: -${discount.toFixed(2)}</p>
      <p>Total before tax: ${totalBeforeTax.toFixed(2)}</p>
      <p>Sales Tax (6%): ${salesTax.toFixed(2)}</p>
      <p>Final Total: ${finalTotal.toFixed(2)}</p>
      <button>Pay with Saved Card</button>
    </div>
  );
};

export default Checkout;
