import React, { useState } from 'react';
import TicketDropdown from './TicketDropdown';

export default function SeatSelect() {
  const totalRows = 4; // A-D
  const totalColumns = 12; // 1-12
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [ticketTypes, setTicketTypes] = useState({});

  const toggleSeatSelection = (seatId) => {
    const isSelected = selectedSeats.includes(seatId);
    setSelectedSeats(isSelected ? selectedSeats.filter(id => id !== seatId) : [...selectedSeats, seatId]);
  };

  const handleTicketTypeChange = (seatId, ticketType) => {
    setTicketTypes({ ...ticketTypes, [seatId]: ticketType });
  };

  return (
    <div className="flex flex-col items-center">
      {Array.from({ length: totalRows }).map((_, rowIndex) => (
        <div key={rowIndex} className="flex justify-center">
          {Array.from({ length: totalColumns }).map((_, columnIndex) => {
            const seatId = `${String.fromCharCode(65 + rowIndex)}${columnIndex + 1}`;
            return (
              <button 
                key={seatId} 
                className={`w-12 h-12 bg-indigo-500 rounded-2 m-1 flex items-center justify-center ${selectedSeats.includes(seatId) ? 'bg-indigo-600' : ''}`} 
                onClick={() => toggleSeatSelection(seatId)}
              >
                <span className="text-m text-white font-semibold">
                  {seatId}
                </span>
              </button>
            );
          })}
        </div>
      ))}
      <div className="flex flex-col items-center mt-4">
        {selectedSeats.map(seatId => (
          <TicketDropdown key={seatId} maxTickets={3} onSelect={(ticketType) => handleTicketTypeChange(seatId, ticketType)} />
        ))}
      </div>
    </div>
  );
}
