import logo from './logo.svg';
import './App.css';
import Login from './Components/Login';
import Register from './Components/Register';
import Navbar from './Components/Navbar';
import React, {useState} from 'react';
import MovieList from "./Components/MovieList"
import Movieinfo from "./Components/MovieInfo"
import Searchbar from './Components/Searchbar';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Update this line

const movies = [
  {
    name: 'Oppenheimer',
    genre: 'Thriller/Action',
    rating: 'R',
    mpaarating: 'MPAA-US R',
    imageUrl:
      'https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcQlbnvo6cBTWi-aLAHChxPCNfCEDDqPWEejYpDvlA3ctbAmOJfVMCmflmEktyLC5wK69EGA',
    runtime: '180 Minutes',
    review: '9/10',
    director: 'Christopher Nolan',
    producer: 'Producer',
    synopsis: 'Brief Movie Description',
    cast: 'List of Actors',
    category: 'Currently Playing',
  },
  {
    name: 'Past Lives',
    genre: 'Romance/Drama',
    rating: 'PG-13',
    mpaarating: 'MPAA-US PG-13',
    imageUrl:
      'https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcSVJSj4a-fYEBTJPV-ns5UszYf--tyTJU3nP8qkgLeJzOi8kWCYbQzYKEzEbbjWjiaJBuvW9Q',
    runtime: '96 Minutes',
    review: '8.5/10',
    director: 'Celine Song',
    producer: 'Producer',
    synopsis: 'Brief Movie Description',
    cast: 'List of Actors',
    category: 'Coming Soon',
  },
]


function App() {

  const [isModalOpen, setModalOpen] = useState(false);
  const [currentFilter, setCurrentFilter] = useState("All")
  const [movieList, setMovieList] = useState(movies)

  function filterFunction(movie) {
    if(currentFilter === "All"){
      return true
    }
    return movie.category === currentFilter
  }

  const handleCurrentFilter = () => {
    var tempList = movies.filter(filterFunction)
    setMovieList(tempList)
    console.log(movieList)
  }

  const updateFilter = (fil) => {
    setCurrentFilter(fil)
    handleCurrentFilter()
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={
          <>
            <Navbar />
            <Searchbar changeFilter={updateFilter}/>
            <MovieList movies={movieList}/>
          </>
        } />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        {/* Add other routes here */}
      </Routes>
    </Router>
  );
}

export default App;
