import React, { useState } from 'react';
import { ReactComponent as CustomSeatIcon } from './customSeatIcon.svg';

// Seat component integrated
const Seat = ({ seatNumber, onSelect }) => {
  const [selected, setSelected] = useState(false);

  const handleSeatClick = () => {
    setSelected(!selected);
    onSelect(seatNumber, !selected);
  };

  return (
    <div
      className={`seat ${selected ? 'selected' : ''}`}
      onClick={handleSeatClick}
    >
      <CustomSeatIcon className="w-6 h-6 fill-current text-white" />
      {seatNumber}
    </div>
  );
};

// SeatMap component integrated
const SeatMap = ({ seats, onSeatSelect }) => {
  return (
    <div className="seat-map">
      {seats.map((seat) => (
        <Seat key={seat.number} seatNumber={seat.number} onSelect={onSeatSelect} />
      ))}
    </div>
  );
};

// SeatSelect component using the integrated SeatMap
export default function SeatSelect({ seats, onSeatSelect }) {
    // Assuming you will pass seats and onSeatSelect as props to SeatSelect
    // or you might fetch or define them here.
    return (
        <>
            <SeatMap seats={seats} onSeatSelect={onSeatSelect} />
        </>
    );
}
