import React, { useState } from 'react';

export default function Profile() {
  const [activeTab, setActiveTab] = useState('info'); // 'info' or 'history'
  const [userInfo, setUserInfo] = useState({
    firstName: 'John',
    lastName: 'Doe',
    phone: '123-456-7890',
    email: 'john.doe@example.com',
    // Add other fields as necessary
  });
  const [orderHistory, setOrderHistory] = useState([
    // Example order history
    { id: 1, item: 'Ticket A', date: '2023-01-01' },
    { id: 2, item: 'Ticket B', date: '2023-02-01' },
    // Add more orders as needed
  ]);

  const handleUserInfoChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Update user information logic here
    console.log('Updated User Info:', userInfo);
  };

  return (
    <>
        <div className="flex">
        <div className="w-1/4 p-4">
            <ul className="flex flex-col">
            <li className={`p-2 ${activeTab === 'info' ? 'bg-gray-200' : ''}`} onClick={() => setActiveTab('info')}>Update Personal Information</li>
            <li className={`p-2 ${activeTab === 'history' ? 'bg-gray-200' : ''}`} onClick={() => setActiveTab('history')}>Order History</li>
            </ul>
        </div>
        <div className="w-3/4 p-4">
            {activeTab === 'info' && (
            <form onSubmit={handleSubmit}>
                <div className="space-y-4">
                <div>
                    <label htmlFor="firstName" className="block">First Name</label>
                    <input type="text" id="firstName" name="firstName" value={userInfo.firstName} onChange={handleUserInfoChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm" />
                </div>
                <div>
                    <label htmlFor="lastName" className="block">Last Name</label>
                    <input type="text" id="lastName" name="lastName" value={userInfo.lastName} onChange={handleUserInfoChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm" />
                </div>
                <div>
                    <label htmlFor="phone" className="block">Phone</label>
                    <input type="text" id="phone" name="phone" value={userInfo.phone} onChange={handleUserInfoChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm" />
                </div>
                <div>
                    <label htmlFor="email" className="block">Email</label>
                    <input type="email" id="email" name="email" value={userInfo.email} onChange={handleUserInfoChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm" />
                </div>
                {/* Add other input fields similarly */}
                </div>
                <button type="submit" className="mt-4 px-4 py-2 bg-blue-500 text-white rounded">Save</button>
            </form>
            )}
            {activeTab === 'history' && (
            <ul>
                {orderHistory.map((order) => (
                <li key={order.id}>{order.item} - {order.date}</li>
                ))}
            </ul>
            )}
        </div>
        </div>
    </>
  );
}
